import React from "react";
import "./testimonial.css";
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";
import $ from "jquery"; // Import jQuery

const Testimonial = () => {
    useEffect(() => {
        // Initialize Owl Carousel when the component mounts
        $(".testimonial-content").owlCarousel({
            loop: true,
            items: 2,
            margin: 50,
            dots: true,
            nav: false,
            mouseDrag: true,
            autoplay: false,
            autoplayTimeout: 4000,
            smartSpeed: 800
        });

        // Clean up Owl Carousel when the component unmounts
        return () => {
            $(".testimonial-content").owlCarousel("destroy");
        };
    }, []); // The empty dependency array ensures this effect runs once after the initial render

    return (
        <section className="testimonial-area">
            <div className="container">
                <div className="sec-title white-title">
                    <h2>Testimonials</h2>
                    <p>What Client Say About Us?</p>
                </div>
                <div className="testimonial-content owl-carousel">
                    {/* Single Testimonial */}
                    <div className="single-testimonial">
                        <div className="round-1 round"></div>
                        <div className="round-2 round"></div>
                        <p>
                            Lorem Ipsum is simply dummy text of the printing and typesetting
                            industry. Lorem Ipsum has been the industry's standard dummy text ever
                            since the 1500s, when an unknown printer took a galley of type and
                            scrambled it to make a type specimen book
                        </p>
                        <div className="client-info">
                            <div className="client-video">
                                <a href="#">
                                    <img src="https://i.ibb.co/DWhSr6S/play-button2.png" alt="" />
                                </a>
                            </div>
                            <div className="client-details">
                                <h6>Yeasin Arafat</h6>
                                <span>Designer, LLCG Team</span>
                            </div>
                        </div>
                    </div>
                    {/* Single Testimonial */}
                    {/* Repeat the structure for additional testimonials */}
                </div>
            </div>
        </section>
    );
};

export default Testimonial;
