import React, { useLayoutEffect } from "react";
import "./Home.css";
import ProfileImg from "/Users/jarvis/Desktop/Porfolio/KESHAV'S portfolio/Portfolio/src/components/minion.png";
import { FaTwitter, FaDribbble, FaBehance } from 'react-icons/fa';
import LocomotiveScroll from "locomotive-scroll";

function checkScroll() {
    console.log(target);
    const scrollPosition = window.pageYOffset;
    const windowSize = window.innerHeight;
    const docHeight = Math.max(
      document.body.scrollHeight,
      document.documentElement.scrollHeight,
      document.body.offsetHeight,
      document.documentElement.offsetHeight,
      document.body.clientHeight,
      document.documentElement.clientHeight
    );
    }  

const Home = React.forwardRef((props, scrollRef) => {
  useLayoutEffect(() => {
    if (scrollRef.current) {
      const scroll = new LocomotiveScroll({
        el: scrollRef.current,
        smooth: true,
      })

      return () => {
        scroll.destroy();
      }
    }
  }, [scrollRef])

  return (
    <section className="home scrollContainer" id="scroll" ref={scrollRef}>
      <div className="home__container container" >
        <p className="home__subtitle text-cs">
          Hello <span>My Name Is</span>
        </p>
        <h1 className="home__title text-cs">
          <span>KESHAV</span> <br />DAYAL
        </h1>

        <p className="home__job">
          <span className="text-cs">I Am</span> <b> Web Developer</b>
        </p>

        <div className="home__img-wrapper">
          <div className="home__banner">
          <img src={ProfileImg} alt="Profile" className="home__profile" />
          </div>
        </div>
        <p className="home__text">
          Lorem ipsum dolor sit amet consectetur adipisicing elit.
          Commodi labore dolorum beatae! Quas quod quasi, quia fugit praesentium aperiam velit?
        </p>

        <div className="home__socials">
          <a href="./" className="home__social-link">
            <FaTwitter aria-label="Twitter profile" />
          </a>
          <a href="./" className="home__social-link">
            <FaDribbble aria-label="Dribbble profile" />
          </a>
          <a href="./" className="home__social-link">
            <FaBehance aria-label="Behance profile" />
          </a>
        </div>
        <div className="home__btns">
          <a href=" " className="btn text-cs">
            Download CV
          </a>
          <a href=" " className="hero__link text-cs">
            My Skills
          </a>
        </div>
      </div>
    </section>
  );
});

export default Home;
